import { Component } from '@angular/core';
import { NgForm } from "@angular/forms";
import { Model } from '../model/calculator-view-model';
import { Service } from "../services/calculator.service";


@Component({
  selector: 'ng-model',
  templateUrl: './model.component.html',
})
export class CalculatorComponent {


  input: string = '';
  result: string = '';
  model = {} as Model;

  constructor(public Service: Service) { }



  pressNum(num: string) {

    //Do Not Allow . more than once
    if (num == ".") {
      if (this.input != "") {

        const lastNum = this.getLastOperand()
        console.log(lastNum.lastIndexOf("."))
        if (lastNum.lastIndexOf(".") >= 0) return;
      }
    }

    //Do Not Allow 0 at beginning. 
    //Javascript will throw Octal literals are not allowed in strict mode.
    if (num == "0") {
      if (this.input == "") {
        return;
      }
      const PrevKey = this.input[this.input.length - 1];
      if (PrevKey === '/' || PrevKey === '*' || PrevKey === '-' || PrevKey === '+') {
        return;
      }
    }

    this.input = this.input + num
    this.calcAnswer();
  }


  getLastOperand() {
    let pos: number;
    console.log(this.input)
    pos = this.input.toString().lastIndexOf("+")
    if (this.input.toString().lastIndexOf("-") > pos) pos = this.input.lastIndexOf("-")
    if (this.input.toString().lastIndexOf("*") > pos) pos = this.input.lastIndexOf("*")
    if (this.input.toString().lastIndexOf("/") > pos) pos = this.input.lastIndexOf("/")
    console.log('Last ' + this.input.substr(pos + 1))
    return this.input.substr(pos + 1)
  }


  pressOperator(op: string) {

    //Do not allow operators more than once
    const lastKey = this.input[this.input.length - 1];
    if (lastKey === '/' || lastKey === '*' || lastKey === '-' || lastKey === '+') {
      this.model.operation = lastKey;
      console.log("Operation " + this.model.operation);
      this.input = '';
      return;
    }

    this.input = this.input
    this.calcAnswer();
  }


  clear() {
    if (this.input != "") {
      this.input = this.input.substr(0, this.input.length - 1)
    }
  }

  allClear() {
    this.result = '';
    this.input = '';
  }

  calcAnswer() {
    let formula = this.input;

    let lastKey = formula[formula.length - 1];

    if (lastKey === '.') {
      formula = formula.substr(0, formula.length - 1);
    }

    lastKey = formula[formula.length - 1];

    if (lastKey === '/' || lastKey === '*' || lastKey === '-' || lastKey === '+' || lastKey === '.') {
      formula = formula.substr(0, formula.length - 1);

    }

    if (this.model.operation === '/' || this.model.operation === '*' || this.model.operation === '-' || this.model.operation === '+') {
      this.model.value2 = +formula;
    }
    else {
      this.model.value1 = +formula;
    }

    console.log("Formula " + formula);
    console.log("input " + this.input);
    console.log("Value1 " + this.model.value1);
    console.log("Value2 " + this.model.value2);


    this.result = eval(formula);
    this.model.operation = '';
  }

  getAnswer() {
    this.calcAnswer();
    this.input = this.result;
    if (this.input == "0") this.input = "";
  }

}
