import { HttpClient, HttpErrorResponse, HttpHeaders } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable, throwError } from 'rxjs';
import { catchError, retry } from 'rxjs/operators';
import { Calculator } from '../model/calculator-view-model';


@Injectable({
  providedIn: 'root'
})
export class CalculatorService {

  url = 'https://localhost:44252/api/Calculator';

  // putting HttpClient
  constructor(private httpClient: HttpClient) { }

  // Headers
  httpOptions = {
    headers: new HttpHeaders({ 'Content-Type': 'application/json' })
  }

  // Calculator
  getCauculator(calculator: Calculator): Observable<Calculator> {
    return this.httpClient.get<Calculator>(this.url + '/' + calculator, this.httpOptions)
      .pipe(
        retry(2),
        catchError(this.handleError)
      )
  }

  // erros
  handleError(error: HttpErrorResponse) {
    let errorMessage = '';
    if (error.error instanceof ErrorEvent) {
      // Erro client
      errorMessage = error.error.message;
    } else {
      // Erro server
      errorMessage = `Error code: ${error.status}, ` + `Msn: ${error.message}`;
    }
    console.log(errorMessage);
    return throwError(errorMessage);
  };

}
